import classes from "./QuizStarted.module.css";
import Card from "../UI/BlurCard/Card";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const QuizStarted = (props) => {
  const [score, setScore] = useState(0);
  const [counter, setCounter] = useState(60);
  const [ansValid , setAnsValid] = useState(null);
  const navigate = useNavigate();

  let quizNumber = 0;

  const ansCheck = (data) => {
    if (data.select == 'yash') {
      setAnsValid(true);
      alert(true);
    } else {
      setAnsValid(false);
      alert(false);
    }
    quizNumber++;
  }

  useEffect(() => {
    counter > 0 && setTimeout(() => setCounter(counter - 1), 1000);
    if (counter == 0) {
      navigate("/");
    };
  }, [counter]);

  const Button = (props) => {
    return (
      <button
        className={`${classes.button} ${ansValid == null ? '' : ansValid ? classes.right : classes.wrong}`}
        type={props.type ? props.type : "button"}
        {...props}
      >
        {props.children}
      </button>
    );
  };

  return (
    <div className={classes.quiz}>
      <div className={classes.book_image}>
        <img src="../../../assets/cardicon.svg" alt="image" />
      </div>
      <p className={classes.title}>English Grammar</p>
      <Card>
        <p className={classes.circle}>
          <h6>{counter}</h6>
          <span>Sec Left</span>
        </p>
        <p className={classes.question}>What is related to the earthquake?</p>
        {[{ opt: 'vasu' , ansValid : false }, { opt: 'jash' }, { opt: 'yash' }].map((item) => (
          <Button onClick={() => ansCheck({ select: item.opt })} >{item.opt}</Button>
        ))}
      </Card>
      <p className={classes.score}>Your Score : {score}</p>
      <div className={classes.box_2}>
        <p>Banner Add</p>
      </div>
    </div>
  );
};
export default QuizStarted;
